package doge

import "testing"

var (
	pizza_tx   = ""
	pizza_hash = "cca7507897abc89628f450e8b1e0c6fca4ec3f7b34cccf55f3f531c659ff4d79"
)

func TestTxHash(t *testing.T) {
	hash := TxHashHex(hx2b(pizza_tx))
	if hash != pizza_hash {
		t.Fatalf("TxHashHex: wrong tx hash: %s vs %s", hash, pizza_hash)
	}
}
